import React from 'react';
import { View, Text, Image, ScrollView, TouchableOpacity, StyleSheet } from 'react-native';
import { fontSize } from 'react-native-rapi-ui/constants/typography';
import Agenda from '../components/Agenda';

export default function Detail({ navigation }) {

  navigation.setOptions({
    headerTitle: 'José'
  })

 return (
   <ScrollView style={styles.container}>
    <Image
      source={require('../assets/pedreiros.jpg')}
      style={styles.image}
      resizeMode="cover"
    />

    <View>
      <View>
        <Text style={[styles.title, {fontSize: 24}]}> José </Text>
      </View>
      <View opacity={0.4}>
        <Text style={[styles.title, {fontSize: 30}]}> Sobre Mim </Text>
      </View>
      <View style={styles.description}>
        <Text style={styles.description}>

        </Text>

        <Text style={styles.price}>
          Preço médio da visita: R$50,00
        </Text>
      </View>

      <Agenda/>

    </View>
   </ScrollView>
  );
}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    width: '100%',
    backgroundColor: '#fff'
  },

  image:{
    width: '100%'
  },

  title: {
    fontFamily: 'Arial',
    paddingHorizontal: '2%',
    marginVertical: '2%'
  },

  description: {
    fontSize: 16,
    lineHeight: 25,
    marginVertical: '2%',
    paddingHorizontal: '2%'
  },

  price: {
    fontSize: 16,
    lineHeight: 25
  }
});
