import React from "react";
import { View, Linking, ScrollView, StyleSheet, TouchableOpacity } from "react-native";
import { MainStackParamList } from "../types/navigation";
import { NativeStackScreenProps } from "@react-navigation/native-stack";
import { supabase } from "../initSupabase";
import {
  Layout,
  Button,
  Text,
  TopNav,
  Section,
  SectionContent,
  useTheme,
  themeColor,
} from "react-native-rapi-ui";
import { Ionicons } from "@expo/vector-icons";
import Categories from "../components/Categories";

export default function ({
  navigation,
}: NativeStackScreenProps<MainStackParamList, "MainTabs">) {
  const { isDarkmode, setTheme } = useTheme();
  return (
    <Layout>
      <TopNav
        middleContent="SOS Lar"
        rightContent={
          <Ionicons
            name={isDarkmode ? "sunny" : "moon"}
            size={20}
            color={isDarkmode ? themeColor.white100 : themeColor.dark}
          />
        }
        rightAction={() => {
          if (isDarkmode) {
            setTheme("light");
          } else {
            setTheme("dark");
          }
        }}
      />
      <View style={styles.container}>

        <ScrollView>
          <Text>Nossos Servi√ßos</Text>

          <View style={styles.categoriesContainer}>
            <Categories style={styles.categories}>
              <Text> Pedreiro </Text>
            </Categories>

            <Categories style={styles.categories}>
              <Text> Design </Text>
            </Categories>
          </View>

          <View style={styles.categoriesContainer}>
            <Categories style={styles.categories}>
              <Text> Pintor </Text>
            </Categories>
            <Categories style={styles.categories}>
              <Text> Cuidador </Text>
            </Categories>
          </View>

          <View style={styles.categoriesContainer}>
            <Categories img>
              <Text> Pedreiro </Text>
            </Categories>
            <Categories style={styles.categories}>
              <Text> Pedreiro </Text>
            </Categories>
          </View>


        </ScrollView>
      </View>
    </Layout>
  );
}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },

  categories: {
    marginTop: '10%',
    padding: '10%',
    justifyContent: 'center',
    alignItems: 'center',
    width: '30%',
    height: '30%'
  },

  categoriesContainer: {
    flexDirection: 'row', 
    justifyContent: 'space-around'
  }
})
