import React from "react";
import { View, Linking, ScrollView, StyleSheet, TouchableOpacity } from "react-native";
import { MainStackParamList } from "../types/navigation";
import { NativeStackScreenProps } from "@react-navigation/native-stack";
import {
  Layout,
  Button,
  Text,
  TopNav,
  Section,
  SectionContent,
  useTheme,
  themeColor,
} from "react-native-rapi-ui";
import { Ionicons } from "@expo/vector-icons";
import Categories from "../components/Categories";
import { Input } from "react-native-elements";

export default function ({
  navigation,
}: NativeStackScreenProps<MainStackParamList, "MainTabs">) {
  const { isDarkmode, setTheme } = useTheme();
  return (
    <Layout>
      <TopNav
        leftContent="SoS Lar"
        middleContent={
          <Input
            placeholder="Busque aqui..."
            style={styles.searchBar}

          />
        }
        rightContent={
          <Ionicons
            name={isDarkmode ? "sunny" : "moon"}
            size={20}
            color={isDarkmode ? themeColor.white100 : themeColor.dark}
          />
        }
        rightAction={() => {
          if (isDarkmode) {
            setTheme("light");
          } else {
            setTheme("dark");
          }
        }}
      />
      <View style={styles.container}>

        <ScrollView>
          <Text>Nossos Servi√ßos</Text>

          <View style={styles.categoriesContainer}>
            <Categories img={require('../assets/pedreiros.jpg')} onPress={() => {
                navigation.navigate("Pedreiros");
              }}>
             Pedreiro
            </Categories>
            <Categories img={require('../assets/design.png')} onPress={() => {
                navigation.navigate("Designers");
              }}>
              Design 
            </Categories>
          </View>

          <View style={styles.categoriesContainer}>
            <Categories img={require('../assets/cuidador.png')} onPress={() => {
                navigation.navigate("Cuidadores");
              }}>
             Cuidador
            </Categories>
            <Categories img={require('../assets/pintura.png')} onPress={() => {
                navigation.navigate("Pintores");
              }}>
              Pintura 
            </Categories>
          </View>

          <View style={styles.categoriesContainer}>
            <Categories img={require('../assets/pedreiro.png')} onPress={() => {
                navigation.navigate("Pedreiros");
              }}>
             Pedreiro
            </Categories>
            <Categories img={require('../assets/pedreiro.png')} onPress={() => {
                navigation.navigate("Pedreiros");
              }}>
              Pedreiro 
            </Categories>
          </View>


        </ScrollView>
      </View>
    </Layout>
  );
}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },

  categories: {
    marginTop: '10%',
    padding: '10%',
    justifyContent: 'center',
    alignItems: 'center',
    width: '30%',
    height: '30%'
  },

  categoriesContainer: {
    flexDirection: 'row', 
    justifyContent: 'space-around'
  },

  searchBar: {

  }
})
